sys_script_include.script:
var TableSchema = Class.create();
TableSchema.prototype = {
    initialize: function() {
    },

	getSchema: function(tablename) {
		var stringCompare = function(a, b) {
			if (a.name > b.name) return 1;
			if (a.name < b.name) return -1;
			return 0;
		};		
		var list = [];
		var grDbo = new GlideRecord('sys_db_object');
		grDbo.addQuery('name', tablename);
		grDbo.query();
		if (grDbo.next()) {
			var hash = this.getTableDefinition(grDbo);
			for (fieldname in hash) {
				list.push(hash[fieldname]);
			}
		}
		list.sort(stringCompare);
		return list;
	},

	getTableDefinition: function(grDbo) {
		var hash = {};
		var tablename = grDbo.getValue('name');
		var grDict = new GlideRecord('sys_dictionary');
		grDict.addQuery('name', tablename);
		grDict.addNotNullQuery('element');
		grDict.addActiveQuery();
		grDict.query();
		while (grDict.next()) {
			var fieldname = grDict.getValue('element');
			var fieldDef = {
				name: fieldname,
				type: grDict.getValue('internal_type'),
				len: grDict.getValue('max_length'),
				ref: grDict.getValue('reference')
			};
			hash[fieldname] = fieldDef;
		}
		if (grDbo.super_class) {
			var grParent = grDbo.super_class.getRefRecord();
			var parentHash = this.getTableDefinition(grParent);
			for (fieldname in parentHash) {
				hash[fieldname] = parentHash[fieldname];
			}
		}
		return hash;
	},

    type: 'TableSchema'
};
