sys_script_include.script:
var JobRunnerMIDServer = Class.create();
JobRunnerMIDServer.prototype = {
    initialize: function() {
    },

	startJobRun: function (grRun) {
		var sys_id = grRun.getUniqueValue();
		var number = grRun.getValue("number");
		var grAgent = grRun.action.table.database_agent.getRefRecord();
		var grScript = grAgent.mid_server_script_file.getRefRecord();
		var scriptPath = this.getPath(grScript);
		var command = 
			grAgent.mid_server_interpreter + " " + scriptPath + " " + sys_id + " " + number;
		var payload = '<?xml version="1.0" encoding="UTF-8"?>' +
			'<parameters><parameter name="name" value="{0}" />' + 
			'<parameter name="skip_sensor" value="true" />' +
			'</parameters>';
		payload = payload.replace("{0}", command);
		var grOutput = new GlideRecord("ecc_queue");
		grOutput.initialize();
		grOutput.agent = "mid.server." + grAgent.mid_server.name;
		grOutput.topic = "Command";
		grOutput.payload = payload;
		grOutput.queue = "output";
		grOutput.state = "ready";
		grOutput.insert();
	},

	// return the path name of a MID Server Script file
	getPath: function(grScript) {
		var path = grScript.parent ?
			this.getPath(grScript.parent.getRefRecord()) + '/' + grScript.name :
			"scripts/" + grScript.name;
		return path;
	},

    type: 'JobRunnerMIDServer'
};
