sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var data = request.body.data;
	gs.info('PutRunStatus request: ' + JSON.stringify(data));
	var idRun = data.sys_id;	
	var grRun = new GlideRecord('x_108443_sndml_action_run');
	if (!idRun || !grRun.get(idRun)) {
		response.setStatus(404); // Not Found
	}
	else {
		var result = new x_108443_sndml.Util().putRunStatus(idRun, data);
		response.setBody(result);
		gs.debug('PutRunStatus response: ' + JSON.stringify(result));
		// check that the status was actually updated
		if (data.status && !data.status.equalsIgnoreCase(result.status)) {
			response.setStatus(403); // Forbidden
			gs.error(gs.getMessage(
				'PutRunStatus NOT UPDATED {0}', [data.sys_id]));
		}
		else {
			response.setStatus(200);
		}		
	}

})(request, response);
