sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var grAgent = new GlideRecord('x_108443_sndml_database_agent');
	if (grAgent.get('name', request.pathParams.agent)) {
		var util = new x_108443_sndml.Util();
		var runs = [];
		var grRun = new GlideRecord('x_108443_sndml_action_run');
		grRun.addQuery('action.database_agent', grAgent.getUniqueValue());
		grRun.addQuery('status', 'ready');
		grRun.query();
		while (grRun.next()) {
			var json = util.buildJSON(grRun);
			runs.push(json);
		}
		if (runs.length > 0)
			gs.info(gs.getMessage(
				'GetRunList {0} {1}', [grAgent.name, JSON.stringify(runs)]));
		var body = {
			'agent': grAgent.getUniqueValue(),
			'agent_name': grAgent.getValue('name'),
			'runs': runs
		};
		gs.info('GetRunList response: ' + JSON.stringify(body));
		response.setBody(body);	
		grAgent.last_checkin = new GlideDateTime();
		grAgent.update();
	}
	else {
		gs.error('GetRunList: Agent Not Found: ' + request.pathParams.agent);
		response.setStatus(404);
	}

})(request, response);
